def search_in_list(data, search_query, column_index):
    search_terms = search_query.split(",")  # ‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö‡∏Å‡∏≤‡∏£‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏´‡∏•‡∏≤‡∏¢‡∏Ñ‡∏≥ ‡πÄ‡∏ä‡πà‡∏ô "term1,term2"
    results = pd.DataFrame()

    for term in search_terms:
        term = term.strip()  # ‡∏ï‡∏±‡∏î‡∏ä‡πà‡∏≠‡∏á‡∏ß‡πà‡∏≤‡∏á
        if term:
            temp_results = data[data.iloc[:, column_index].astype(str).str.contains(term, case=False, na=False)]
            results = pd.concat([results, temp_results])  # ‡∏£‡∏ß‡∏°‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå
    return results.drop_duplicates().reset_index(drop=True)

# ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏à‡∏±‡∏î‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏•‡∏¥‡∏™‡∏ï‡πå‡πÉ‡∏´‡πâ‡πÅ‡∏™‡∏î‡∏á‡∏ú‡∏•‡πÉ‡∏ô‡∏ö‡∏£‡∏£‡∏ó‡∏±‡∏î‡πÉ‡∏´‡∏°‡πà
def format_list_as_multiline(value):
    if isinstance(value, str) and value.startswith('[') and value.endswith(']'):
        try:
            items = eval(value)  # ‡πÅ‡∏õ‡∏•‡∏á string ‡πÉ‡∏´‡πâ‡πÄ‡∏õ‡πá‡∏ô list
            if isinstance(items, list):
                return "\n".join([f"{i+1}. {item}" for i, item in enumerate(items)])
        except:
            pass
    return value

# ‡πÅ‡∏õ‡∏•‡∏á‡∏Ñ‡∏≠‡∏•‡∏±‡∏°‡∏ô‡πå‡∏ó‡∏µ‡πà‡∏°‡∏µ‡∏•‡∏¥‡∏™‡∏ï‡πå‡πÉ‡∏´‡πâ‡πÄ‡∏õ‡πá‡∏ô‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°
def format_dataframe(dataframe):
    formatted_df = dataframe.copy()
    for col in formatted_df.columns:
        formatted_df[col] = formatted_df[col].apply(format_list_as_multiline)
    return formatted_df

